    const [started, setStarted] = useState(false);
    let intervalId
    useEffect(() => {
        if (!started) {
            setStarted(true)
            startTimer();
        }
    });

    function startTimer() {
        intervalId = setInterval(() => {
            setMytime(countDown(time));
        }, 1000);
    }

    function countDown(time) {
        let hours = time[0];
        let minutes = time[1];
