                }
                setHours(h)
            }
            setMinutes(m)
        }
        setSeconds(s)
        console.log(seconds)
        return [h, m, s]
    }

    return (
        <div>
            {children(hours, minutes, seconds)}
        </div>
    );
};

export default Timer;
