import React, {useEffect, useState} from 'react';

const Timer = ({children, settings, onComplete}) => {
    const [time, setTime] = useState([0, 0, 5])

    const [started, setStarted] = useState(false);
    let intervalId
    useEffect(() => {
        if (!started) {
            setStarted(true)
            startTimer();
        }
    });

    function startTimer() {
        intervalId = setInterval(() => {
            setTime((time)=>{let hours = time[0];
                let minutes = time[1];
                let seconds = time[2];

                let h = hours;
                let m = minutes;
                let s = seconds - 1;


                if (s === -1) {
                    let m = minutes - 1;
                    s = 59;
                    if (m === -1){
                        let h = hours -1;
                        m = 59;
                        if (h === -1){
                            s=0;
                            m=0;
                            h=0;
                        }
                    }
                }
                console.log(seconds)
                return [h, m, s]});
        }, 1000);
    }

    function countDown(time) {
        let hours = time[0];
        let minutes = time[1];
        let seconds = time[2];

        let h = hours;
        let m = minutes;
        let s = seconds - 1;


        if (s === -1) {
            let m = minutes - 1;
            s = 59;
            if (m === -1){
                let h = hours -1;
                m = 59;
                if (h === -1){
                    s=0;
                    m=0;
                    h=0;
                }
            }
        }
        console.log(seconds)
        return [h, m, s]
    }

    return (
        <div>
            {children(time[0], time[1], time[2])}
        </div>
    );
};

export default Timer;
