    const [want_to_start, setWantToStart] = useState(true);
    let intervalId
    useEffect(() => {
        if (want_to_start) {
            setWantToStart(false)
            startTimer();
        }
    }, [want_to_start]);

    function startTimer() {
        intervalId = setInterval(() => {
            setTime((time)=>{
